import { Component, OnInit } from '@angular/core';
import { LoaderService } from 'src/app/core/services/loader.service';
import * as Jimp from 'jimp';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import { AuditoriaService } from 'src/app/servicios/auditoria.service';
import { SessionService } from 'src/app/servicios';
import {ActivatedRoute, Router} from '@angular/router';
import * as moment from 'moment-timezone';

@Component({
  selector: 'app-expediente',
  templateUrl: './expediente.component.html',
  styleUrls: ['./expediente.component.css']
})
export class ExpedienteComponent implements OnInit {

  documento: any;
  menuDesplegado = true;
  expediente: any = {};
  documents: any = {};
  signers: Array<any> = [];
  printDetail: boolean;

  constructor(
    private router: Router,
    private loaderService: LoaderService,
    public auditoriaService: AuditoriaService,
    private sessionService: SessionService,
    private route: ActivatedRoute,
  ) { }

  ngOnInit() {
    this.cargarExpediente();
    this.printDetail = false;
  }

  collapseMenu() {
    this.menuDesplegado = !this.menuDesplegado;
  }

  cargarExpediente() {
    this.documento = {
      documentoSerial: this.route.snapshot.paramMap.get('serial').toLowerCase()
    };
    this.auditoriaService.obtenerExpediente(this.documento.documentoSerial).subscribe(async (Respuesta: any) => {
      if (Respuesta.statusCode === 200 || Respuesta.statusCode === 204 || Respuesta.statusCode === 201) {
        this.expediente = Respuesta.data;
        for (const document of this.expediente.documentsTrace) {
          this.documents = document;
          this.signers = document.signers;
        }

        for (const sign of this.signers) {
          sign.mostrarFirma = sign.graphBase64 !== null && sign.graphBase64 !== '';
          sign.sello = await this.addESignatureStamp(sign.userProcessId);
          sign.firma = `data:image/${sign.graphExtesion};base64,${sign.graphBase64}`;
        }


      } else if (Respuesta.statusCode === 401) {
        this.sessionService.logout();
      } else {
        this.router.navigateByUrl('main/errorMifirma', { skipLocationChange: true });
      }
    });
  }

  generarPDF() {
    this.loaderService.startLoading();
    const id = document.getElementById('detailprint');
    id.style.padding = '0 30px';
    const pdf = new jsPDF('p', 'pt', 'a4');
    const headerImg = 'data:image/png;base64,';

    const options = {
      background: null,
      scale: 3
    };
    const vp = document.querySelector('meta[name="viewport"]').getAttribute('content');
    document.querySelector('meta[name="viewport"]').setAttribute('content', 'width=1360, initial-scale=1');
    html2canvas(id, options).then((canvas) => {
      const img = canvas.toDataURL('image/PNG');
      // Add image Canvas to PDF
      const bufferX = 15;
      const bufferY = 15;
      const imgProps = (pdf as any).getImageProperties(img);
      const pdfWidth = pdf.internal.pageSize.getWidth() - 2 * bufferX;
      const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;
      const pdfHeightFooter = pdf.internal.pageSize.getHeight()  - 2 * bufferY;

      //pdf.addFont('Montserrat.ttf', 'sans-serif', 'normal');
      //pdf.setFont('sans-serif', 'normal');

      pdf.addImage(headerImg, 'PNG', pdfWidth - 178, 10, 178, 55);
      pdf.text('Expediente', bufferX + 10, bufferY + 100);
      pdf.addImage(img, 'PNG', bufferX, bufferY + 125, pdfWidth, pdfHeight + bufferY, undefined, 'MEDIUM');
      const footerTextWeb = 'www.mifirma.co';
      pdf.setFontSize(10);
      pdf.text('Firma tus documentos electrónicamente', bufferX, pdfHeightFooter);
      const xPositionText = pdfWidth - (pdf.getStringUnitWidth(footerTextWeb) * pdf.getFontSize() / 2);
      // console.log(pdfWidth - (pdf.getStringUnitWidth(footerTextWeb) * pdf.getFontSize() / 2), pdf.getStringUnitWidth(footerTextWeb), pdfWidth);
     // pdf.setFont('Montserrat', 'bold');
      //pdf.addFont('Roboto-Medium.ttf', 'Roboto', 'normal');
      pdf.text(footerTextWeb, xPositionText - 2 * bufferX, pdfHeightFooter);
      return pdf;
    }).then((docResult) => {


      docResult.save(`${moment().format('DD-MM-YYYYTHH:mm:ss')}_expediente.pdf`);
      document.querySelector('meta[name="viewport"]').setAttribute('content', vp);
      id.style.padding = '0';
      this.loaderService.stopLoading();
    });
  }

  async addESignatureStamp(otp?: string): Promise<string> {
    const fontOtherSigners = '/assets/fonts/open-sans-16-black.txt';
    const selloPath = '/assets/img/img-esign4x.png';
    let aditionalData =  '';
    if (this.expediente.cosmosId !== null) {
      aditionalData = otp ? `ID Firma: ${otp} ` : '';
    }

    const sello = await Jimp.read(selloPath);
    const image = new Jimp(600, 150);

    image.opacity(0.3);
    const font = await Jimp.loadFont(fontOtherSigners);
    image.print(
      font, 0, 0,
      {
        text: aditionalData,
        alignmentX: Jimp.HORIZONTAL_ALIGN_RIGHT,
        alignmentY: Jimp.VERTICAL_ALIGN_BOTTOM
      },
      image.bitmap.width,
      image.bitmap.height
    );

    sello.composite(image, 0, 0);

    return await sello.getBase64Async(Jimp.MIME_PNG);
  }

  totalFirmasDocumento() {
    const totalFirmas = this.signers.filter(x => x.available === true).map(item => item.signaturesNumber).reduce((prev, next) => prev + next, 0);

    return totalFirmas;
  }

  totalFirmantes() {
    const total = this.signers.filter(x => x.available === true);

    return total.length;
  }


}
